import styles from './Login.module.scss';
import { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Dodaj import useHistory

const Login = ({ setLoggedIn }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory(); // Inicjalizacja useHistory

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = {
      username: username,
      password: password
    };

    try {
      const response = await fetch('http://127.0.0.1:8000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      });
      if (response.ok) {
        console.log('Logged in successfully');
        setLoggedIn(true); // Ustawienie stanu zalogowania na true
        history.push('/films'); // Przekierowanie do /films po zalogowaniu
      } else {
        console.error('Login error');
      }
    } catch (error) {
      console.error('An error occurred while sending the request:', error);
    }
  };

  return (
    <div className={styles.login}>
      <h2>Logowanie</h2>
      <form className={styles.loginForm} onSubmit={handleSubmit}>
        <label htmlFor="username">Nazwa użytkownika:</label>
        <input type="text" id="username" name="username" required value={username} onChange={(e) => setUsername(e.target.value)} />

        <label htmlFor="password">Hasło:</label>
        <input type="password" id="password" name="password" required value={password} onChange={(e) => setPassword(e.target.value)} />

        <input type="submit" value="Zaloguj się" />
      </form>
    </div>
  );
};

export default Login;
